	// Coordinate conversion: lat/lon -> SVG coordinates
	// x = (lon - 9.99) * 5940.3, y = -(lat - 53.55) * 10000
	function toSVG(lat: number, lon: number): [number, number] {
		return [(lon - 9.99) * 5940.3, -(lat - 53.55) * 10000];
	}

	function makePath(coords: number[][], closed = true): string {
		const parts = coords.map((c: number[], i: number) => {
			const [x, y] = toSVG(c[0], c[1]);
			return `${i === 0 ? 'M' : 'L'}${x.toFixed(1)},${y.toFixed(1)}`;
		});
		if (closed) parts.push('Z');
		return parts.join(' ');
	}

