<svg
	xmlns="http://www.w3.org/2000/svg"
	viewBox="-200 -350 500 450"
	width="100%"
	height="100%"
	preserveAspectRatio="xMidYMid meet"
>
	<defs>
		<!-- Glow filter for water bodies -->
		<filter id="waterGlow" x="-20%" y="-20%" width="140%" height="140%">
			<feGaussianBlur in="SourceGraphic" stdDeviation="3" result="blur" />
			<feComposite in="SourceGraphic" in2="blur" operator="over" />
		</filter>

		<!-- Stronger glow filter for the marathon route -->
		<filter id="routeGlow" x="-30%" y="-30%" width="160%" height="160%">
			<feGaussianBlur in="SourceGraphic" stdDeviation="4" result="blur1" />
			<feGaussianBlur in="SourceGraphic" stdDeviation="1.5" result="blur2" />
			<feMerge>
				<feMergeNode in="blur1" />
				<feMergeNode in="blur2" />
				<feMergeNode in="SourceGraphic" />
			</feMerge>
		</filter>

		<!-- Subtle outer glow for water stroke -->
		<filter id="waterStrokeGlow" x="-20%" y="-20%" width="140%" height="140%">
			<feGaussianBlur in="SourceGraphic" stdDeviation="2" result="blur" />
			<feMerge>
				<feMergeNode in="blur" />
				<feMergeNode in="SourceGraphic" />
			</feMerge>
		</filter>

